import styled from "styled-components";

const TypeBox = styled.textarea`

        resize: none;
        width: 18.5em;
        height: 13.375em;
        background: #FFFFFF;
        border-radius: 0.3125em;
        font-family: Monteserrat-regular;

    @media(max-width: 1024px)
    {
        #digitacao{
            width: 98.09%;
        }
    }
`

export { TypeBox }
